    const DeskCardHandles=()=>{

        return(
            <div>
            <div className="deskBtmQeus1 btmQues1" id="deskBtmQeus1">
                                <div className="cardIcons" onClick={click == false ? ShowComment : HideComment} style={{cursor: 'pointer'}}>
